                    <div className="form-group">
                        <input
                            type="text"
                            ref="members"
                            placeholder="Who is/are involved?"
                            onChange={this.onChangeMembers}
                        />
                    </div>
                    <div className="form-group">
                        <input type="submit"/>
                    </div>
                </form>
            </div>
		);
